import React, { useState, useEffect } from 'react';
import Contador from './components/Contador';
import './style.css'

export default function App() {

  const [seconds, setSeconds] = React.useState(0);
  const [minutes, setMinutes] = React.useState(0);
  const [hours, setHours] = React.useState(0);
  const [days, setDays] = React.useState(0);
  const goalDate = new Date("Aug 18, 2021 23:59:59").getTime();

  const goal = () => {
    const now = new Date().getTime();
    const interval = goalDate - now;
    let s = 1000;
    let m = s * 60;
    let h = m * 60;
    let d = h * 24;
    console.log(interval);
    if (interval !== 0) {
      setDays(Math.floor(interval / d));
      setHours(Math.floor((interval % d) / h));
      setMinutes(Math.floor((interval % h) / m));
      setSeconds(Math.floor((interval % m) / s));
    }

  };


  useEffect(() => {
    setTimeout(() => {
      goal();
    }, 1000);
  }, [seconds]);

  return (
    <div className="container">
      <Contador value={days} title={days > 1 ? "Dias" : "Dia"} />
      <Contador value={hours} title="horas" />
      <Contador value={minutes} title="minutos" />
      <Contador value={seconds} title="segundos" />
    </div>
  );
}
